mapper.select:
select post.*, post_basic.content from post inner join post_basic on post.seq = post_basic.seq where post.seq in ( select seq_post from post_tag where tag in
#{item}
) and disclosure=1
or (disclosure=2 and seq_blog in (select seq_user from user_neighbor where seq_neighbor= #{seq_user} )) or (seq_blog = #{seq_user})

select post.*, post_basic.content from post inner join post_basic on post.seq = post_basic.seq where seq_blog = #{seq_blog}
and (disclosure=1 or (disclosure=2 and seq_blog in (select seq_user from user_neighbor where seq_neighbor= #{seq_user} )) or (seq_blog = #{seq_user}))
and disclosure=1

select post.*, post_basic.content from post inner join post_basic on post.seq = post_basic.seq where seq_blog = #{seq_blog} and seq in ( select seq_post from post_tag where tag in
#{item}
)
and (disclosure=1 or (disclosure=2 and seq_blog in (select seq_user from user_neighbor where seq_neighbor= #{seq_user} )) or (seq_blog = #{seq_user}))
and disclosure=1

mapper.update:
update post 
   set title = #{title}, 
   	   disclosure = #{disclosure}
 where seq = #{seq}

update post_basic 
   set content = #{content}
 where seq = #{seq}
